import React, { useContext, useState, useEffect } from "react";
import { Pane, Heading, Text, Paragraph } from "evergreen-ui";

import { firebaseContext } from "../context/firebase";

function timeConverter(UNIX_timestamp) {
  var a = new Date(UNIX_timestamp * 1000);
  var months = [
    "Jan",
    "Feb",
    "Mar",
    "Apr",
    "May",
    "Jun",
    "Jul",
    "Aug",
    "Sep",
    "Oct",
    "Nov",
    "Dec"
  ];
  var year = a.getFullYear();
  var month = months[a.getMonth()];
  var date = a.getDate();
  var hour = a.getHours();
  var min = a.getMinutes();

  min = min === 0 ? "00" : "0";

  var time = hour + ":" + min + ", " + date + " " + month + " " + year;
  return time;
}

function List() {
  const firebase = useContext(firebaseContext);
  const [events, setEvents] = useState([]);

  useEffect(() => {
    firebase.db.collection("events").onSnapshot(
      snapshot => {
        const events = snapshot.docs.map(doc => {
          return doc.data();
        });
        setEvents(events);
      },
      err => console.log(err.message)
    );
  }, []);

  function renderEvents() {
    return (
      <ul>
        {events.map((event, index) => {
          console.log(event);
          return (
            <li key={index}>
              <Pane
                elevation={1}
                float="left"
                margin={24}
                padding={24}
                display="flex"
                flexDirection="column"
              >
                <Heading size={700} marginTop={0}>
                  {event.Name}
                </Heading>
                <Text color="muted">{timeConverter(event.Date.seconds)}</Text>
                <Paragraph size={300} marginTop="default">
                  {event.Description}
                </Paragraph>
              </Pane>
            </li>
          );
        })}
      </ul>
    );
  }

  return <div>{renderEvents()}</div>;
}

export default List;

